import React, { Component } from 'react';
import { Card, CardTitle, Col, Row, Button, Modal, Input } from 'react-materialize';
import { connect } from 'react-redux';
import { fetchPosts, deletePost, createPost, votePosts, editPosts } from '../actions';
import Order from './OrderComponent';
import uuidv4 from 'uuid/v4';


class PostsComponent extends Component {
	state={
		isOpen: false,
		isEOpen: false,
		body: '',
		title: '',
		author: '',
		category: '',
		index: null,
	}

	openModal = () => {
		this.setState({isOpen: true});
	}

	openEModal = (index) => {
		this.setState({isEOpen: true});
		this.setState({index});
		console.log(index)
	}
	
	closeModal = () => {
		this.setState({isOpen: false});
		this.setState({body: ''});
		this.setState({title: ''});
		this.setState({author: ''});
		this.setState({category: ''})
	}

	closeEModal = () => {
		this.setState({isEOpen: false});
		this.setState({body: ''});
		this.setState({title: ''});
		this.setState({index: null});
	}

	deletePost = (index) => {
		let {posts} = this.props.posts;
		let id = this.props.posts['posts'][index].id;
		posts.splice(index, 1);
		this.props.disablePost(id, posts);
	}

	createPost = () => {
		const {author, title, body, category} = this.state;
		if(author && body && title && category){
			const uuid = uuidv4;
			let posts = this.props.posts['posts'];
			let post = {};
			
			post.category = category;
			post.title = title;
			post.author = author;
			post.body = body;
			post.timestamp = Date.now();
			post.id = uuid();

			this.props.addPost(post, posts);
		}
		this.closeModal()
	}

	vote = ({option, id, index}) => {
		const {posts} = this.props.posts;
		if('upVote' === option){
			posts[index].voteScore++;
		}else if('downVote' === option){
			posts[index].voteScore--;
		}

		this.props.postVote(id, option, posts);
	}

	updatePost = () => {
		let posts = this.props.posts['posts'];
		let post = this.props.posts['posts'][this.state.index];
		if(this.state.body){
			post.body = this.state.body
		}
		if(this.state.title){
			post.title = this.state.title
		}
		posts[this.state.index] = post;
		this.props.editPost(post, posts);
		this.closeEModal();
	}

	componentDidMount(){
		this.props.getPosts()
		if(this.props.posts['post']){
			alert("post deletado com sucesso")
		}
	}

	render(){
		const {posts} = this.props.posts;
		const {categories} = this.props.categories;
		const imageURL = "data:image/jpeg;base64,";
		const display = {display: 'block'};
		const hide = {display: 'none'};
		return (
			<div>
				<Order/>
				{posts != null &&
					<Row>
						{posts.map((post, index) =>	
							<Col s={6} m={4} key={index}>
								<Card className='large'
									key={post.title}
									header={<CardTitle image={imageURL}>{post.title}</CardTitle>}
									actions={[<a key={post + index} href={"/" + post.category + "/" + post.id}>Know more about this</a>, 
									<Button key={'but' + index}
										floating 
										className='red '
										icon="clear" 
										value={index} 
										onClick={() => this.deletePost(index)}/>,
										<Button key={index + post}	icon="edit"
											onClick={() => this.openEModal(index)}/>]}>
									<b>Post By: {post.author}</b>	
									<p >{post.body.substr(0, 50)}...</p>
									<p >there are {post.commentCount} comments for this post</p>
									<span>Readers review: {post.voteScore}
										<Button floating icon='thumb_up' onClick={() => this.vote({option: 'upVote', id: post.id, index})} className='clear'/>
										<Button floating icon='thumb_down' onClick={() => this.vote({option: 'downVote', id: post.id, index})} className='clear'/>
									</span>
								</Card>
							</Col>
						)}
						<div className='fixed-action-btn'>
							<Button floating icon='add' className='red' large onClick={this.openModal}/>
						</div>
					</Row>
				}
				{this.state.isOpen &&
					<Modal fixedFooter
						header='New Post'
						actions={
							<div>
								<button className="btn waves-effect waves-light btn-flat modal-action modal-close" onClick={this.closeModal}>Close</button>
								<button className="btn waves-effect waves-light btn-flat modal-action modal-confirm" onClick={this.createPost}>Create</button>
							</div>
						}
						style={this.state.isOpen ? display : hide}>
						<div>
							<Input type='select' onChange={(event) => this.setState({category: event.target.value})}
								icon='empty' 
								label='Category'>
								<option value=''>Categories</option>
								{categories.map((category, index) => 
									<option key={index} value={category.name}>{category.name}</option>
								)}
							</Input>
							<Input
								type="text"
								label="UserName"
								value={this.state.author}
								onChange={(event) => this.setState({author: event.target.value})}
								/>
							<Input
								type="text"
								label="Title"
								value={this.state.title}
								onChange={(event) => this.setState({title: event.target.value})}
								/>
							<Input
								type="textarea"
								label="Post"
								value={this.state.body}
								onChange={(event) => this.setState({body: event.target.value})}
								/>
						</div>
					</Modal>
				} 
				{this.state.isEOpen &&
					<Modal fixedFooter
						header='Edit post'
						actions={
							<div>
								<button className="btn waves-effect waves-light btn-flat modal-action modal-close" onClick={this.closeEModal}>Close</button>
								<button className="btn waves-effect waves-light btn-flat modal-action modal-confirm" onClick={this.updatePost}>Save</button>
							</div>
						}
						style={this.state.isEOpen ? display : hide}>
						<div>
							<Input
								type="text"
								defaultValue={posts.title}
								label="Title"
								onChange={(event) => this.setState({title: event.target.value})}
								/>
							<Input
								type="textarea"
								defaultValue={posts.body}
								label="Post"
								onChange={(event) => this.setState({body: event.target.value})}
								/>
						</div>
					</Modal>
				}
			</div>
		)
	}
}

function mapStateToProps ({ posts, categories }) {
	return {
		posts,
		categories
	}
}

function mapDispatchToProps (dispatch) {
	return {
		getPosts: () => dispatch(fetchPosts()),
		disablePost: (id, posts) => dispatch(deletePost(id, posts)),
		editPost: (post, posts) => dispatch(editPosts(post, posts)),
		addPost: (post, posts) => dispatch(createPost(post, posts)),
		postVote: (id, option, posts) => dispatch(votePosts(id, option, posts)),
	}
}

export default connect(
	mapStateToProps,
	mapDispatchToProps
)(PostsComponent)